import {
  createAsyncThunk,
  createSlice,
 } from '@reduxjs/toolkit'

import axios from 'axios';

const initialState = {
  stations: [],
  loading: false,
  error: '',
}


export const fetchStations = createAsyncThunk(
  'stations/fetchStations',
  () => {
    let url = 'https://api.tidesandcurrents.noaa.gov/mdapi/prod/webapi/stations.json?type=tidepredictions'
    axios
      .get(url)
      .then((response) => response.data.stations)
  }
)



export const stationsSlice = createSlice({
  name: 'stations',
  initialState,
  reducers: {
  },
  extraReducers: builder => {
    builder.addCase(fetchStations.pending, state => {
      state.loading = true;
    })
    builder.addCase(fetchStations.fulfilled, (state, action) => {
      state.loading = false
      state.stations = action.payload
      state.error = ''
    })
    builder.addCase(fetchStations.rejected, (state, action) => {
      state.loading = false
      state.stations = []
      state.error = action.error.message
    })
  },
})

export const selectStations = (state) => state.stations;

export default stationsSlice.reducer
