import {
  createAsyncThunk,
  createSlice,
 } from '@reduxjs/toolkit'

import axios from 'axios';

const initialState = {
  stations: [],
  loading: false,
  error: '',
}


export const fetchStations = createAsyncThunk(
  'stations/fetchStations',
  async () => {
    let url = 'https://api.tidesandcurrents.noaa.gov/mdapi/prod/webapi/stations.json?type=tidepredictions'
    const response = await axios.get(url)
    return response.data.stations
  }
)



export const stationsSlice = createSlice({
  name: 'stations',
  initialState,
  reducers: {
  },
  extraReducers: (builder) => {
    builder.addCase(fetchStations.fulfilled, (state, action) => {
      state.projects = action.payload
    })
  },
})

// Action creators are generated for each case reducer function

export const selectStations = (state) => state.stations.stations


export default stationsSlice.reducer
