import React, { useState, useEffect } from 'react';

import {
  CircleMarker
} from 'react-leaflet'


const Station = (props) => {

const { stationInfo } = props

const [predictions, setPredictions] = useState([])

const position = [stationInfo?.lat, stationInfo?.lng]

const overlayStyle = {
  opacity: '.25',
  transform: 'rotate3d(1, 0, 0, 45deg)',
}

const circleRadius = 10

let args = [
  { station: '8454000'},
  {range: '24'},
  {product: 'water_level'},
  {datum: 'MSL'},
  {units: 'english'},
  {time_zone: 'gmt'},
  {format: 'json'},
]

const urlWithArguments = (args) => {
    let returnURl =  'https://api.tidesandcurrents.noaa.gov/api/prod/datagetter'
                       + args.map((argument) => '&'
                                  + Object.keys(argument)[0]
                                  + '='
                                  + argument[Object.keys(argument)[0]])
                                  .join('')
    return returnURl
}



  return (
    <>
       <CircleMarker center={position} pathOptions={stationMarker} radius={3} />
    </>
)
}

const stationMarker = { color: 'blue', opacity: '.2' }


export default Station
