import React, {useState} from 'react';

import { useSelector } from 'react-redux';

import L from 'leaflet';

import {
  Marker,
  Popup,
} from 'react-leaflet'

import 'leaflet/dist/leaflet.css';

import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';



const Epicycles = (props) => {

  let DefaultIcon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow,
    iconAnchor: [0, 0],
    popupAnchor: [0,0],
  });

  L.Marker.prototype.options.icon = DefaultIcon;



  // let popupOpen = L.Popup.prototype.__proto__.isOpen()

  // console.log(popupOpen)

  // console.log(Object.getOwnPropertyNames(L.Popup.prototype.__proto__))


  const { stationInfo } = props

  const position = [stationInfo?.lat, stationInfo?.lng]

  return (
    <>
    <Marker
    position={position}
    autoPan={true}
    autoPanOnFocus={true}
    riseOnHover={true}
    eventHandlers={{
      click: () => {
        console.log('marker clicked')
      },
    }}
    >
      <Popup
      autoPanPadding={[500, 250]}
      autoPan={true}
      autoClose={false}>
          <div>

          </div>
      </Popup>
    </Marker>
    </>
)
}


export default Epicycles
