import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  value: 0,
  status: 'idle',
};

export const timeIndexSlice = createSlice({
  name: 'timeIndex',
  initialState,
  reducers: {
    increment: (state) => {
      state.value += 1;
    },
    decrement: (state) => {
      state.value -= 1;
    },
    incrementByAmount: (state, action) => {
      state.value += action.payload;
    },
    zero: (state) => {
      state.value = 0;
    }
  },
});

export const { increment, decrement, incrementByAmount, zero } = timeIndexSlice.actions;

export const selectCount = (state) => state.timeIndex.value;

export default timeIndexSlice.reducer;
